import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, Button } from 'react-native';
// import CameraRoll from "@react-native-community/cameraroll";
import * as Contacts from 'expo-contacts';
import * as Permissions from 'expo-permissions';

export default function App() {

  const [permissions, setPermissions] = useState(false);

  const showContacts = async () => {
    // get all my phone contacts
    const contactList = await Contacts.getContactsAsync();
    setContacts(contactList.data);
  }

  const getPermissions = async () => {
    const {status} = await Permissions.askAsync(Permissions.CONTACTS);
    console.log({status});
    if(status === 'granted'){
      setPermissions(true);
    } else { setPermissions(false) }
  }

  useEffect( () => {
    getPermissions();
  }, [])

  // const savePicture = async () => {
  //   CameraRoll.save(tag, { type, album })
  // };

  // abc() {
  //   getPackages().add(new CameraRoll())
  // }

  const showAlbums = async () => {
    // const albumsList = await CameraRoll.getAlbums("All");
    // console.log(albumsList.count)
  }

  return (
    <View style={styles.container}>
      <Text>Hello, World!</Text>

      <Button
        onPress={showContacts}
        title="show contacts"
      ></Button>

      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
